<?php /* Template Name: References */ ?><?php get_header(); ?><div class="row">    <div class="blog-main">        <?php            if (have_posts()) {                while (have_posts()) {                    the_post();                    $image = (has_post_thumbnail(get_the_ID())) ? get_the_post_thumbnail_url( get_the_ID(), 'post-thumbnail') : get_background_image();                    echo "<section class='cover animatedParent animateOnce' style='background-image: url($image);'>";                    echo '<section class="cover-title animatedParent animateOnce"><h1 class="animated fadeInDownShort">'; echo the_title(); echo "</h1> </a></section>";                    echo "</section>";                    echo "<section class='content-wrapper'>";                    the_content();                    ?>                    <section class="clickimage-wrapper">                        <?php                            $media_query = new WP_Query(                                array(                                    'post_type' => 'attachment', // Get only attachments.                                    'post_mime_type' => 'image', // Get only images.                                    'post_status' => 'inherit', // Get post status from parent object (if post is disabled attachment will too).                                    'posts_per_page' => -1, // get all.                                    'post_parent__not_in' => array(0) // Get only attachments that are actually attached to something.                                )                            );                            foreach ($media_query->posts as $post) {                                echo "<section id='clickimage' class='clickimage'>";                                echo wp_get_attachment_link($post->ID);                                echo "</section>";                            }                        ?>                    </section>        <?php                }            }        ?>        <?php echo "</section>"; ?>    </div></div><?php get_footer(); ?>